    private void btnNovoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnNovoActionPerformed
        CadastroFuncionarioJD telaCadastro = new CadastroFuncionarioJD(this, rootPaneCheckingEnabled);
        telaCadastro.setVisible(true);

        Funcionario novoFuncionario = telaCadastro.getFuncionario();
        if (novoFuncionario != null) {
            try {
                //JOptionPane.showMessageDialog(rootPane, novoVendedor);
                dao.persist(novoFuncionario);
                loadFuncionarios();
            } catch (Exception ex) {
                System.err.println("Erro ao cadastrar Funcionário: " + ex);
            }

        }
    }//GEN-LAST:event_btnNovoActionPerformed

    private void btnInfoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnInfoActionPerformed
        if (tblFuncionarios.getSelectedRow() != -1) {
            Funcionario obj = (Funcionario) tblFuncionarios.getModel().getValueAt(tblFuncionarios.getSelectedRow(), 0);
            JOptionPane.showMessageDialog(rootPane, obj.exibirDados());
        } else {
            JOptionPane.showMessageDialog(rootPane, "Selecione um funcionário");
        }
    }//GEN-LAST:event_btnInfoActionPerformed

    private void btnRemoverActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnRemoverActionPerformed
        if (tblFuncionarios.getSelectedRow() != -1) {
            Funcionario obj = (Funcionario) tblFuncionarios.getModel().getValueAt(tblFuncionarios.getSelectedRow(), 0);
            int op_remover = JOptionPane.showConfirmDialog(rootPane, "Tem certeza que deseja remover " + obj + "?");
            if (op_remover == JOptionPane.YES_OPTION) {
                try {
                    dao.remover(obj);
                } catch (Exception ex) {
                    System.err.println("Erro ao remover funcionário: " + ex);
                }
                JOptionPane.showMessageDialog(rootPane, "Funcionário removido com sucesso... ");
                loadFuncionarios();
            }

        } else {
            JOptionPane.showMessageDialog(rootPane, "Selecione um funcionário");
        }
    }//GEN-LAST:event_btnRemoverActionPerformed

    private void btnEditarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEditarActionPerformed
        if (tblFuncionarios.getSelectedRow() != -1) {
            Funcionario obj = (Funcionario) tblFuncionarios.getModel().getValueAt(tblFuncionarios.getSelectedRow(), 0);
            CadastroFuncionarioJD telaEdicao = new CadastroFuncionarioJD(this, rootPaneCheckingEnabled);
            telaEdicao.setFuncionario(obj);

            telaEdicao.setVisible(true);

            Funcionario obj_retorno = telaEdicao.getFuncionario();
            if (obj_retorno != null) {
                try {
                    dao.persist(obj_retorno);
                    loadFuncionarios();
                } catch (Exception ex) {
                    System.out.println("Erro ao editar Funcionário: " + ex);
                }
            }

        } else {
            JOptionPane.showMessageDialog(rootPane, "Selecione um funcionário");
        }
    }//GEN-LAST:event_btnEditarActionPerformed

