    public void loadFuncionarios() {

        // Obtém o modelo da tabela - vincular o que definimos no Desing
        DefaultTableModel modelo = (DefaultTableModel) tblFuncionarios.getModel();
        //limpar as linhas e popular 
        modelo.setNumRows(0);

        for (Funcionario funcionario : dao.listaFuncionarios()) {
            Object[] linha = {
                funcionario,
                funcionario.getCPF(),
                funcionario.getSalario(),
            };
            modelo.addRow(linha);
        }

    }

